import React, { Component } from "react";
import ReactDOM from "react-dom";
import Player from "./player";
import "./styles.css";

const moves = ["rock", "paper", "scissors"];
const modes = ["bestOf3", "bestOf5"];
const types = ["random", "tactical"];

class App extends Component {
  state = { 
    player: moves[0], //Keep track of player selection
    computer: moves[0], //Keep track of computer selection
    lastChoice: moves[0], //Keep track of computer's last selection
    mode: modes[0], // Keep track of mode(i.e how many rounds)
    type: types[0], // keep track of Computer type
    playerScore: 0, // keep track of Player's Score
    computerScore: 0, // keep track of Computer's Score
    round:null,
    nextBest:"",
    winner: ""
  };

  startGame = () => {
    let counter = 0;
    let gameInterval = setInterval(() => {
      counter++;
      if(this.type === "tactical"){
        this.nextBest = this.selectNextBestMove()
        this.playTactical() //Play tactically
      }else{
        this.lastChoice = this.playRandom() //Play random for Computer
      }
      if (counter > 5) {
        clearInterval(gameInterval);
        this.setState({
          winner: this.selectWinner()
        });
      }
    }, 100);
  };

  //selectMode
  selectMode = mode => {
    let round = 0;
    if (mode === "bestOf3"){
      round = 3;
    }else if(mode === "bestOf5"){
      round = 5;
    }else{
      return "Select Mode"
    }
    this.setState({
      mode: mode,
      round: round
    });
  };
  //select Computer Type
  selectType = type => {
    if(type ==="tactical"){
      type = "tactical"
    }else{
      type = "random"
    }
    this.setState({
      type: type,
      });
  };
  selectWinner = (playerScore, computerScore) => {
    const { player, computer,  } = this.state;

    if (player === computer) {
      return "It's a Draw!";
    } else if (
      (player === "rock" && computer === "scissors") ||
      (player === "scissors" && computer === "paper") ||
      (player === "paper" && computer === "rock")
    ) {
      this.updatePlayerScore(this.playerScore)
      return "Player!";
    } else {
      // this.updateComputerScore()
      return "Computer!";
    }
  };
  selectMove = move => {
    this.setState({
      player: move,
      winner: ""
    });
  };
  //playRandom
  playRandom = () =>{
    let move =""
    move = moves[Math.floor(Math.random() * moves.length)]
    this.lastChoice = move
    this.setState({
      computer: move,
      lastChoice: this.lastChoice,
    });
  }
  playTactical = () =>{
    let move ="", nextBest =""
    nextBest = this.selectNextBestMove()
    move = this.playTactical(this.lastChoice)
    this.lastChoice = move
    this.setState({
      computer: move,
      lastChoice: this.lastChoice,
      nextBest: nextBest
    });
  }
  // update Player Score
  updatePlayerScore = (playerScore) => {
    this.setState({
      playerScore: playerScore +1,
      });
  }

  // update Computers Score
  updateComputerScore = (computerScore) => {
    computerScore = this.co
    this.setState({
      computerScore: computerScore +1,
      });
  }

  //update next Best
  updateNextBest = (nextBest) => {
    this.setState({ nextBest: nextBest});
  }
  //selectNextBestMove
  selectNextBestMove = () => {
    const {  lastChoice } = this.state;

    switch(lastChoice) {
      case 'rock':
        this.updateNextBest("paper")
        return 'paper';
      case 'paper':
        this.updateNextBest("scissors")
        return 'scissors';
      case 'scissors':
        this.updateNextBest("rock")
        return 'rock';
      default:
        return this.playRandom();
    }
  };
  render() {
    const { player, computer, winner } = this.state;
    return (
      <>
        <h1 style={{ textAlign: "center" }}>Rock Paper Scissors</h1>
        <div class="gamesetting">
        <button
            className="moveBtn"
            onClick={() => this.selectType("random")}
          >
            Random
          </button>
        <button
            className="moveBtn"
            onClick={() => this.selectType("tactical")}
          >
            Tactical AI
          </button>
          <button
            className="moveBtn"
            onClick={() => this.selectMode("bestOf3")}
          >
            Best Of 3
          </button>
          <button
            className="moveBtn"
            onClick={() => this.selectMode("bestOf5")}
          >
            Best Of 5
          </button>

        </div>
        <div>
          <Player move={player} />
          <Player move={computer} />
        </div>
        <div>
          <button
            className="moveBtn"
            onClick={() => this.selectMove("rock")}
          >
            rock
          </button>
          <button
            className="moveBtn"
            onClick={() => this.selectMove("paper")}
          >
            paper
          </button>
          <button
            className="moveBtn"
            onClick={() => this.selectMove("scissors")}
          >
            scissor
          </button>
        </div>
        <div className="winner">Winner: {winner ? this.selectWinner(this.playerScore, this.computerScore) : null}</div>
        <button type="button" onClick={this.startGame}>
          Play!
        </button>
      </>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
